mapper.select:
 		select
	bno, name, phone, address, message, viewCnt, category
from musteat_night;

select viewCnt 
  from musteat_viewCnt 
where userId = #{userId}
    and commentId = #{bno}

mapper.update:
update musteat_viewCnt 
      set viewCnt = viewCnt + 1 
 where userId = #{userId} 
     and commentId = #{bno}

mapper.select:
select count(*) 
  from musteat_coupon 
where userId = #{userId}
   and commentId = #{bno}
